  bool access::object_traits_impl< ::Alarmud::user, id_mysql >::
  grow (image_type& i,
        my_bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id
    //
    t[0UL] = 0;

    // nickname
    //
    if (t[1UL])
    {
      i.nickname_value.capacity (i.nickname_size);
      grew = true;
    }

    // email
    //
    if (t[2UL])
    {
      i.email_value.capacity (i.email_size);
      grew = true;
    }

    // registered
    //
    t[3UL] = 0;

    // password
    //
    if (t[4UL])
    {
      i.password_value.capacity (i.password_size);
      grew = true;
    }

    // level
    //
    t[5UL] = 0;

    // backup_email
    //
    if (t[6UL])
    {
      i.backup_email_value.capacity (i.backup_email_size);
      grew = true;
    }

    // ptr
    //
    t[7UL] = 0;

    return grew;
  }

  void access::object_traits_impl< ::Alarmud::user, id_mysql >::
  bind (MYSQL_BIND* b,
        image_type& i,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    std::size_t n (0);

    // id
    //
    if (sk != statement_update)
    {
      b[n].buffer_type = MYSQL_TYPE_LONGLONG;
      b[n].is_unsigned = 1;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // nickname
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.nickname_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.nickname_value.capacity ());
    b[n].length = &i.nickname_size;
    b[n].is_null = &i.nickname_null;
    n++;

    // email
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.email_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.email_value.capacity ());
    b[n].length = &i.email_size;
    b[n].is_null = &i.email_null;
    n++;

    // registered
    //
    b[n].buffer_type = MYSQL_TYPE_DATETIME;
    b[n].buffer = &i.registered_value;
    b[n].is_null = &i.registered_null;
    n++;

    // password
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.password_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.password_value.capacity ());
    b[n].length = &i.password_size;
    b[n].is_null = &i.password_null;
    n++;

    // level
    //
    b[n].buffer_type = MYSQL_TYPE_SHORT;
    b[n].is_unsigned = 1;
    b[n].buffer = &i.level_value;
    b[n].is_null = &i.level_null;
    n++;

    // backup_email
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.backup_email_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.backup_email_value.capacity ());
    b[n].length = &i.backup_email_size;
    b[n].is_null = &i.backup_email_null;
    n++;

    // ptr
    //
    b[n].buffer_type = MYSQL_TYPE_TINY;
    b[n].is_unsigned = 0;
    b[n].buffer = &i.ptr_value;
    b[n].is_null = &i.ptr_null;
    n++;
  }

