  bool access::object_traits_impl< ::Alarmud::legacy, id_mysql >::
  grow (image_type& i,
        my_bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // name
    //
    if (t[0UL])
    {
      i.name_value.capacity (i.name_size);
      grew = true;
    }

    // realname
    //
    if (t[1UL])
    {
      i.realname_value.capacity (i.realname_size);
      grew = true;
    }

    // email1
    //
    if (t[2UL])
    {
      i.email1_value.capacity (i.email1_size);
      grew = true;
    }

    // email2
    //
    if (t[3UL])
    {
      i.email2_value.capacity (i.email2_size);
      grew = true;
    }

    return grew;
  }

  void access::object_traits_impl< ::Alarmud::legacy, id_mysql >::
  bind (MYSQL_BIND* b,
        image_type& i,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    std::size_t n (0);

    // name
    //
    if (sk != statement_update)
    {
      b[n].buffer_type = MYSQL_TYPE_STRING;
      b[n].buffer = i.name_value.data ();
      b[n].buffer_length = static_cast<unsigned long> (
        i.name_value.capacity ());
      b[n].length = &i.name_size;
      b[n].is_null = &i.name_null;
      n++;
    }

    // realname
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.realname_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.realname_value.capacity ());
    b[n].length = &i.realname_size;
    b[n].is_null = &i.realname_null;
    n++;

    // email1
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.email1_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.email1_value.capacity ());
    b[n].length = &i.email1_size;
    b[n].is_null = &i.email1_null;
    n++;

    // email2
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.email2_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.email2_value.capacity ());
    b[n].length = &i.email2_size;
    b[n].is_null = &i.email2_null;
    n++;
  }

